mapper.select:
select *
from File
where 1=1
and fileId = #{fileId}
and userId = #{userId}

mapper.update:
update File
set security=#{security}
  , security2=#{security2}
  , securityName=#{securityName}
  , securityDetail=#{securityDetail}
where fileId=#{fileId}

mapper.delete:
delete from File
where 1=1
and fileId = #{fileId}
and userId = #{userId}

delete from Comment
where 1=1
and id = #{id}
